#include <iostream>#include <vector>using namespace std;void merge(vector<int>& nums1, int m, vector<int>& nums2, int n) {    int i = 0, j = 0, k = 0;    vector<int> temp(n + m);    while (i < m && j < n) {        if (nums1[i] > nums2[j]) {            temp[k++] = nums2[j++];        } else {            temp[k++] = nums1[i++];        }    }    while (i < m || j < n) {        if (i < m) {            temp[k++] = nums1[i++];        } else {            temp[k++] = nums2[j++];        }    }    for (int i = 0; i < n + m; i++) {        nums1[i] = temp[i];    }}int main() {    int m, n;    cout << "Enter the number of elements in nums1: ";    cin >> m;    cout << "Enter the number of elements in nums2: ";    cin >> n;    vector<int> nums1(m);    cout << "Enter the elements of nums1:\n";    for (int i = 0; i < m; i++) {        cin >> nums1[i];    }       vector<int> nums2(n);    cout << "Enter the elements of nums2:\n";    for (int i = 0; i < n; i++) {        cin >> nums2[i];    }    // Extend nums1 with zeroes to make space for nums2    nums1.resize(m + n);    merge(nums1, m, nums2, n);    cout << "Merged array: ";    for (int i = 0; i < m + n; i++) {        cout << nums1[i];        cout << ",";    }    cout << endl;    return 0;}